//&#x5305;&#x88C5;&#x6267;&#x884C;&#x51FD;&#x6570;
function run(generator) {
    var it = generator(), ret;
    (function iterator(value) {
        ret = it.next(value);
        if (!ret.done) {
            if (ret.value.then) {
                ret.value.then(iterator);
            } else {
                Promise.resolve(ret.value).then(iterator);
            }
        }
    })();
}

